import { Box } from "@mui/material";
import React, { useContext, useEffect, useState } from "react";
import ChatNavbar from "../components/chat/ChatNavbar";
import ChatMessageInputs from "../components/chat/ChatMessageInputs";
import Message from "../components/chat/Message";
import { themeContext } from "../context/ThemeContext";
import { useParams } from "react-router-dom";
import Nomessages from "../components/etc/Nomessages";
import { getChatRoom } from "../utils/ChatRoomApi";
import { socketContext } from "../context/SocketContext";

export default function Chat() {
  const { mode } = useContext(themeContext);
  const [load, setLoad] = useState(false);
  const { socket } = useContext(socketContext);
  const [chatRoom, setChatRoom] = useState();
  const receiver = useParams();

  const ChatRoom = async () => {
    const chatData = await getChatRoom(receiver.id);
    // console.log(chatData);
    setChatRoom(chatData);
  };

  useEffect(() => {
    ChatRoom();
  }, [receiver]);

  useEffect(() => {
    const waitSocket = async () => {
      const newSocket = await socket;

      if (!newSocket) return 

      return newSocket
    }
    if (socket) {
      socket.on("message", (data) => {
        setLoad(!load)
        const chatMessages = chatRoom?.messages;
        console.log(chatMessages);
        chatMessages.push(data);
        console.log(data);
        setChatRoom({ ...chatRoom, messages: chatMessages });
      });
    }else {
      waitSocket()
    }

    setLoad(!load);
  }, []);

  return (
    <Box
      sx={{
        width: "100%",
        display: "flex",
        flexDirection: "column",
        justifyContent: "space-between",
      }}
    >
      <ChatNavbar
        receiverData={chatRoom?.members.find(
          (member) => member._id === receiver.id
        )}
      />

      <Box
        sx={{
          height: "100vh",
          overflowX: "hidden",
          overflowY: "auto",
          "&::-webkit-scrollbar": {
            height: 2,
            width: 2,
            WebkitAppearance: "none",
          },
          "&::-webkit-scrollbar-thumb": {
            borderRadius: 0,
            border: "2px solid",
          },
        }}
      >
        <Box
          sx={{
            width: "100%",
            height: "auto",
            minHeight: "100%",
            flex: 1,
            display: "flex",
            flexDirection: "column",
            justifyContent: "flex-end",
            backgroundColor: mode ? "" : "rgba(24, 24, 24, 1)",
          }}
        >
          {chatRoom?.messages.length === 0 ? (
            <Nomessages />
          ) : (
            chatRoom?.messages.map((msg) => {
              return (
                <Message
                  key={msg._id}
                  messages={msg}
                  receiver={receiver.id}
                  receiverData={chatRoom?.members.find(
                    (member) => member._id === receiver.id
                  )}
                  senderData={chatRoom?.members.find(
                    (member) => member._id !== receiver.id
                  )}
                />
              );
            })
          )}
        </Box>
      </Box>

      <ChatMessageInputs path={receiver.id} />
    </Box>
  );
}
