import React, { createContext, useContext, useEffect, useState } from "react";
import { tokenContext } from "../context/TokenContext";
import io from "socket.io-client";

const socketContext = createContext();

export default function SocketContext({ children }) {
  const [socket, setSocket] = useState();
  const [onlineUsers, setOnlineUsers] = useState([]);
  const { token } = useContext(tokenContext);

  useEffect(() => {
    if (token) {
      const receiverSocketId = io("http://localhost:8000/", {
        query: {
          authorization: token,
        },
      });

      console.log(receiverSocketId);

      receiverSocketId.on("message", (message) => {
        console.log(message);
      })

      setSocket(receiverSocketId);
      // socket.on() is used to listen to the events. can be used both on client and server side
      receiverSocketId.on("getOnlineUsers", (users) => {
        setOnlineUsers(users);
      });

      return () => receiverSocketId.close();
    } else {
      if (socket) {
        socket?.close();
        setSocket("");
      }
    }
  }, []);

  return (
    <socketContext.Provider value={{ socket, onlineUsers }}>
      {children}
    </socketContext.Provider>
  );
}

export { socketContext };
