import { Box } from "@mui/material";
import React, { useContext, useEffect, useRef, useState } from "react";
import ChatNavbar from "../components/chat/ChatNavbar";
import ChatMessageInputs from "../components/chat/ChatMessageInputs";
import Message from "../components/chat/Message";
import { themeContext } from "../context/ThemeContext";
import { userContext } from "../context/UserContext";
import { useParams } from "react-router-dom";

export default function Chat() {
  const { users, userloading } = useContext(userContext);
  const [user, setUser] = useState();
  const { mode } = useContext(themeContext);
  const { id } = useParams();
  const lastMessageRef = useRef(null);

  useEffect(() => {
    function findUser(userId) {
      return users.find((user) => user._id === userId);
    }

    if (!userloading) {
      let userData = findUser(id);
      setUser(userData);
    }
  }, [userloading, id]);

  return (
    <Box
      sx={{
        width: "100%",
        display: "flex",
        flexDirection: "column",
        justifyContent: "space-between",
      }}
    >
      <ChatNavbar user={user} />

      <Box
        sx={{
          width: "100%",
          height: "100vh",
          overflowX: "hidden",
          overflowY: "auto",
          "&::-webkit-scrollbar": {
            height: 2,
            width: 2,
            WebkitAppearance: "none",
          },
          "&::-webkit-scrollbar-thumb": {
            borderRadius: 0,
            border: "2px solid",
          },
        }}
      >
        <Box
          sx={{
            width: "100%",
            height: "auto",
            minHeight: "100%",
            flex: 1,
            display: "flex",
            flexDirection: "column",
            justifyContent: "flex-end",
            backgroundColor: mode ? "" : "rgba(24, 24, 24, 1)",
          }}
        >
          <Message ref={lastMessageRef} />
        </Box>
      </Box>

      <ChatMessageInputs path={id} />
    </Box>
  );
}
