import React, { useContext, useEffect } from "react";
import { Box, Avatar } from "@mui/material";
import { themeContext } from "../../context/ThemeContext";
import { Link, useParams } from "react-router-dom";

export default function UserCard({ user }) {
  const { mode } = useContext(themeContext);
  const { _id, fullName, profilePic } = user;

  return (
    <Link to={_id}>
      <Box
        sx={{
          paddingX: "24px",
          paddingY: "12px",
          display: "flex",
          alignItems: "center",
          cursor: "pointer",
          gap: "16px",
          backgroundColor: params?.id === _id ? mode
          ? "rgba(0, 163, 255, 0.15)"
          : "rgba(0, 163, 255, 0.30)" : "",
          ":hover": {
            backgroundColor: mode
              ? "rgba(0, 163, 255, 0.15)"
              : "rgba(0, 163, 255, 0.30)",
          },
        }}
      >
        <Avatar src={profilePic} sx={{ width: 58, height: 58 }} />

        <Box
          sx={{
            width: "100%",
            display: "flex",
            alignItems: "center",
            justifyContent: "space-between",
          }}
        >
          <Box>
            <h2 className="text-[18px] font-medium">{fullName}</h2>
            <h3 className="text-[#00A3FF] text-[14px] font-normal">Online</h3>
          </Box>

          <h3 className="text-[#A0A0A0] text-[14px]">14:00</h3>
        </Box>
      </Box>
    </Link>
  );
}
