const token = localStorage.getItem("token");

async function getChatRooms() {
  try {
    const response = await fetch("http://localhost:8000/api/room/chatrooms", {
      method: "GET",
      headers: {
        authorization: token,
      },
    });

    if (response.status !== 200) {
        window.location.reload()
      throw new Error("ChatRoom not found");
    }

    const res = await response.json();
    return res.data;
  } catch (error) {
    console.log(error);
  }
}
async function getChatRoom(id) {
  try {
    const response = await fetch(`http://localhost:8000/api/room/chatroom/${id}`, {
      method: "GET",
      headers: {
        authorization: token,
      },
    });

    if (response.status !== 200) {
      console.log(response)
      throw new Error("ChatRoom not found");
    }

    const res = await response.json();
    return res?.data;
  } catch (error) {
    console.log(error);
  }
}

export { getChatRooms, getChatRoom };
