import React, { createContext, useContext, useEffect, useReducer, useState } from "react";
import { getChatRooms } from "../utils/ChatRoomApi";
import { socketContext } from "./SocketContext";

const chatRoomContext = createContext();

export default function ChatRoomContext({ children }) {
  const [chatRoomLoading, setChatRoomLoading] = useState("null");
  const [room, setRoom] = useState([]);
  const {socket} = useContext(socketContext)
  const [ChatRoom, ChatRoomDispatch] = useReducer(async (state, action) => {
    let data = null;
    setChatRoomLoading(true);
    switch (action.type) {
      case "getChatRooms":
        data = await getChatRooms();
        setChatRoomLoading(false);
        setRoom(data);
        return data;

      case "addChatRoom":
        data = [...state, action.payload];
        setRoom(data);
        return data;

      default:
        return [...state];
    }
  }, []);

  return (
    <chatRoomContext.Provider
      value={{ room, ChatRoomDispatch, chatRoomLoading }}
    >
      {children}
    </chatRoomContext.Provider>
  );
}

export { chatRoomContext };
