import React, { createContext, useEffect, useReducer, useState } from "react";
import { getChatRooms } from "../utils/ChatRoomApi";

const chatRoomContext = createContext();

export default function ChatRoomContext({ children }) {
  const [chatRoomLoading, setChatRoomLoading] = useState(false);
  const [ChatRoom, ChatRoomDispatch] = useReducer(async (state, action) => {
    switch (action.type) {
      case "getChatRooms":
        setChatRoomLoading(true);
        try {
          const data = await getChatRooms();
          
          if (data.data) {
              return data.data;
          } else {
            return [...state];
          }
        } catch (error) {
          console.log(error);
          return state;
        } finally {
          setChatRoomLoading(false);
        }

      default:
        return [...state];
    }
  }, []);

  useEffect(() => {
    ChatRoomDispatch({ type: "getChatRooms" });
  }, []);

  useEffect(() => {
    if (!chatRoomLoading) {
        console.log(ChatRoom);
    }
  }, [chatRoomLoading])

  return (
    <chatRoomContext.Provider value={{ ChatRoom, ChatRoomDispatch }}>
      {children}
    </chatRoomContext.Provider>
  );
}

export { chatRoomContext };
