import React, { useEffect, useRef, useState } from "react";
import KeyboardVoiceIcon from "@mui/icons-material/KeyboardVoice";
import StopIcon from "@mui/icons-material/Stop";
import { toast } from "react-toastify";
import "./style.css";
import image from "../../../assets/images";

export default function AudioRecorder({ onTouched, setOnTouched }) {
  const audioRef = useRef();
  const visualizer = useRef();
  const [items, setItems] = useState([]);
  const [recorder, setRecorder] = useState(null);

  // Audio recording analyser
  window.AudioContext = window.AudioContext || window.webkitAudioContext;

  const recordering = (audio) => {
    let ctx = new window.AudioContext();
    let analyser = ctx.createAnalyser();
    let source = ctx.createMed();

    source.connect(analyser);
    source.connect(ctx.destination);
    analyser.fftSize = 64;
    let bufferLength = analyser.frequencyBinCount;
    let dataArray = new Uint8Array(bufferLength);
    let elements = [];

    for (let i = 0; i < bufferLength; i++) {
      let element = document.createElement("span");
      element.classList.add("element");
      elements.push(element);
      visualizer?.current.appendChild(element);
    }

    let element = document.createElement("span");
    element.classList.add("element");
    visualizer?.current.appendChild(element);

    let update = () => {
      requestAnimationFrame(update);
      analyser.getByteFrequencyData(dataArray);
      for (let i = 0; i < bufferLength; i++) {
        let item = dataArray[i];
        elements[i].style.setProperty("--i", `${item}px`);
      }
    };

    update();
  };

  const startRecording = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      const recorderInstance = new MediaRecorder(stream);
      setRecorder(recorderInstance);

      recorderInstance.ondataavailable = (e) => {
        const newData = [...items, e.data];
        let blob = new Blob(newData, { type: "audio/webm" });
        let url = window.URL.createObjectURL(blob)
        setItems(url);

        recordering()
      };

      recorderInstance.start();
    } catch (error) {
      console.error("MediaDevices error:", error);
    }
  };

  const stopRecording = () => {
    if (recorder) {
      recorder.stop();
    }
  };

  // Audio recorder
  useEffect(() => {
    if (onTouched) {
      startRecording()
    }else {
      stopRecording()
    }
  }, [onTouched]);

  return true ? (
    <div className=" absolute top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2">
      <audio className=" absolute -top-20 left-0" src={items} controls ref={audioRef}></audio>
      <div className="box">
        <div className="visualizer" ref={visualizer}></div>
      </div>
      <div className="play">
        <div className="btn btn-play">
          {onTouched ? (
            <StopIcon sx={{ width: "100px", fontSize: 150 }} />
          ) : (
            <KeyboardVoiceIcon sx={{ width: "100px", fontSize: 150 }} />
          )}
        </div>
      </div>
    </div>
  ) : (
    ""
  );
}
