import React, { useContext, useEffect, useState } from "react";
import { Box, Avatar } from "@mui/material";
import { themeContext } from "../../context/ThemeContext";
import { Link } from "react-router-dom";
import { socketContext } from "../../context/SocketContext";

export default function UserCard({ chatRoom }) {
  const { mode } = useContext(themeContext);
  const [online, setOnline] = useState(false);
  const { onlineUsers } = useContext(socketContext);
  const { fullName, profilePic, _id } = chatRoom?.receiverMember;
  const messages = chatRoom?.messages;

  function calcTime(date) {
    const time = date.split("T")[1].split(".");

    return time[0];
  }

  useEffect(() => {
    onlineUsers.forEach((userId) => {
      if (userId === _id) {
        setOnline(true);
      }
    });
  }, []);

  return (
    <Link to={_id}>
      <Box
        sx={{
          paddingX: "24px",
          paddingY: "12px",
          display: "flex",
          alignItems: "center",
          cursor: "pointer",
          gap: "16px",
          ":hover": {
            backgroundColor: mode
              ? "rgba(0, 163, 255, 0.15)"
              : "rgba(0, 163, 255, 0.30)",
          },
        }}
      >
        <Avatar src={profilePic} sx={{ width: 58, height: 58 }} />

        <Box
          sx={{
            width: "100%",
            display: "flex",
            alignItems: "center",
            justifyContent: "space-between",
          }}
        >
          <Box>
            <h2 className="text-[18px] font-medium">{fullName}</h2>
            <h3 className="text-[#00A3FF] text-[14px] font-normal">
              {/* {messages[messages.length - 1].message} */}
              {online ? "Online" : "Offline"}
            </h3>
          </Box>

          <h3 className="text-[#A0A0A0] text-[14px]">
            {calcTime(messages[messages.length - 1].createdAt)}
          </h3>
        </Box>
      </Box>
    </Link>
  );
}
