import React, { useContext, useEffect, useState } from "react";
import { Box, Avatar } from "@mui/material";
import { themeContext } from "../../context/ThemeContext";
import { Link } from "react-router-dom";
import { calcTime } from "../../utils/helper";

export default function FindUser({ chatRoom, checkOnline, onlineUsers, rol, findSearch }) {
  const { mode } = useContext(themeContext);
  const { fullName, profilePic, _id } = chatRoom?.receiverMember;
  const [online, setOnline] = useState(false);

  useEffect(() => {
    const isCheck = checkOnline(_id);
    setOnline(isCheck);
  }, [onlineUsers, chatRoom]);

  return (
    <Link to={newchat_id}>
      <Box
        sx={{
          paddingX: "24px",
          paddingY: "12px",
          display: "flex",
          alignItems: "center",
          cursor: "pointer",
          gap: "16px",
          ":hover": {
            backgroundColor: mode
              ? "rgba(0, 163, 255, 0.15)"
              : "rgba(0, 163, 255, 0.30)",
          },
        }}
      >
        <Avatar src={profilePic} sx={{ width: 58, height: 58 }} />

        <Box
          sx={{
            width: "100%",
            display: "flex",
            alignItems: "center",
            justifyContent: "space-between",
          }}
        >
          <Box>
            <h2 className="text-[18px] font-medium">{fullName}</h2>
            <h3 className="text-[#00A3FF] text-[14px] font-normal">
              {online ? "Online" : "Offline"}
            </h3>
          </Box>
        </Box>
      </Box>
    </Link>
  );
}
