import React, { useContext, useReducer } from "react";
import Sidebar from "./Sidebar";
import Chat from "./Chat";
import { Route, Routes } from "react-router-dom";
import Nomessages from "../components/etc/Nomessages";
import NotFound from "../components/etc/NotFound";
import { socketContext } from "../context/SocketContext";

export default function ChatApp() {
  const { onlineUsers } = useContext(socketContext);
  const [active, actDispatch] = useReducer((state, action) => {
    switch (action.type) {
      case "SET_ACTIVE":
        return action.id;
      default:
        return state;
    }
  }, "");

  const isOnline = (id) => {
    return onlineUsers.some((user) => user._id === id);
  }
 
  return (
    <>
      <Sidebar active={active} />
      <Routes>
        <Route path="/">
          <Route index element={<Nomessages sty={true} />} />
          <Route path=":id" element={<Chat actDispatch={actDispatch} />} />
          <Route path=":id/*" element={<NotFound />} />
        </Route>
        <Route path="*" element={<NotFound />} />
      </Routes>
    </>
  );
}
