import "./style.css"

export default function AudioRecorder({onTouched, setOnTouched}) {
  return (
    <div className=" abs">
      <audio hidden></audio>
      <div className="box">
        <div className="visualizer"></div>
      </div>
      <div className="play">
        <div className="btn btn-play"></div>
      </div>
    </div>
  );
}
