import React, { useContext, useEffect } from "react";
import Sidebar from "./Sidebar";
import Chat from "./Chat";
import { Route, Routes } from "react-router-dom";
import Nomessages from "../components/etc/Nomessages";
import NotFound from "../components/etc/NotFound";

export default function ChatApp() {

  return (
    <>
      <Sidebar onlineUsers={onlineUsers} checkOnline={checkOnline} />
      <Routes>
        <Route path="/">
          <Route index element={<Nomessages sty={true} />} />
          <Route
            path=":id"
            element={
              <Chat onlineUsers={onlineUsers} checkOnline={checkOnline} />
            }
          />
          <Route
            path="newchat/:id"
            element={
              <NewChat onlineUsers={onlineUsers} checkOnline={checkOnline} />
            }
          />
          <Route path="newchat" element={<NotFound />} />
        </Route>
        <Route path="*" element={<NotFound />} />
      </Routes>
    </>
  );
}
