import { Box, Paper } from "@mui/material";
import PlayArrowIcon from '@mui/icons-material/PlayArrow';
import { AudioVisualizer } from 'react-audio-visualize';
import React, { useState } from "react";

export default function AudioFileMessage({ bg, file }) {
  const [blob, setBlob] = useState();
  return (
    <Box className={`${bg} rounded-md p-3 flex items-center gap-2`}>
      <Paper
        elevation={3}
        sx={{ p: 1, display: "inline-block", borderRadius: "100%", cursor: "pointer" }}
      >
        <PlayArrowIcon sx={{ width: "35px", height: "35px" }} />
      </Paper>

      <Box sx={{ display: "flex", flexDirection: "column", gap: 0.6 }}>
      <AudioVisualizer
          blob={file}
          width={500}
          height={75}
          barWidth={1}
          gap={0}
          barColor={'#f76565'}
        />
      </Box>
    </Box>
  );
}
