import React, { createContext, useEffect, useReducer } from "react";
import { getRoomMessage } from "../utils/MessageApi";

const messagesContext = createContext();

export default function MessageContext({ children }) {
  const [messages, MsgDispatch] = useReducer(async (state, action) => {
    switch (action.type) {
      case "getMessage":
        const data = await getRoomMessage(action.id);
        console.log(data);
        return;
      default:
        return state;
    }
  }, []);

  useEffect(() => {})

  return (
    <messagesContext.Provider value={{}}>{children}</messagesContext.Provider>
  );
}

export { messagesContext };
