import { Box } from "@mui/material";
import React, { useContext, useEffect, useRef, useState } from "react";
import ChatMessageInputs from "../components/chat/ChatMessageInputs";
import ChatNavbar from "../components/chat/ChatNavbar";
import { themeContext } from "../context/ThemeContext";
import { socketContext } from "../context/SocketContext";
import { useParams } from "react-router-dom";
import { getChatRoom } from "../utils/ChatRoomApi";
import Nomessages from "../components/etc/Nomessages";

export default function NewChat({ onlineUsers, checkOnline }) {
  const { mode } = useContext(themeContext);
  const [load, setLoad] = useState(false);
  const { socket } = useContext(socketContext);
  const [chatRoom, setChatRoom] = useState();
  const receiver = useParams();
  const lastMessageRef = useRef(null);

  const ChatRoom = async () => {
    const chatData = await getChatRoom(receiver.id);
    setChatRoom(chatData);
    console.log(chatData);
  };

  const updateChatRoom = async (msg) => {
    setChatRoom((prevState) => {
      return {
        ...prevState,
        messages: [...prevState.messages, msg],
      };
    });
  };

  useEffect(() => {
    ChatRoom();
  }, [receiver]);

  useEffect(() => {
    setTimeout(() => {
      if (lastMessageRef.current) {
        lastMessageRef.current?.scrollIntoView({ behavior: "smooth" });
      }
    }, 100);
  }, [chatRoom]);

  useEffect(() => {
    if (socket && chatRoom) {
      socket.on("newMessage", (msg) => {
        const { message, chatId } = msg;
        const id = chatRoom?._id;
        console.log(chatId, id);
        console.log(chatRoom);
        if (chatId == id) {
          setChatRoom((prevState) => {
            return {
              ...prevState,
              messages: [...prevState.messages, message],
            };
          });
        }
      });
      return () => socket?.off("newMessage");
    } else setLoad(!load);
  }, [load, chatRoom]);

  return (
    <>
      <Box
        sx={{
          width: "100%",
          display: "flex",
          flexDirection: "column",
          justifyContent: "space-between",
        }}
      >
        <ChatNavbar
          receiverData={chatRoom?.members.find(
            (member) => member._id === receiver.id
          )}
          onlineUsers={onlineUsers}
          checkOnline={checkOnline}
        />

        <Box
          sx={{
            height: "100vh",
            overflowX: "hidden",
            overflowY: "auto",
            "&::-webkit-scrollbar": {
              height: 2,
              width: 2,
              WebkitAppearance: "none",
            },
            "&::-webkit-scrollbar-thumb": {
              borderRadius: 0,
              border: "2px solid",
            },
          }}
        >
          <Box
            sx={{
              width: "100%",
              height: "auto",
              minHeight: "100%",
              flex: 1,
              display: "flex",
              flexDirection: "column",
              justifyContent: "flex-end",
              backgroundColor: mode ? "" : "rgba(24, 24, 24, 1)",
            }}
          >
            {chatRoom?.messages.length === 0 ? (
            <Nomessages />
          ) : (
            chatRoom?.messages.map((msg, idx) => {
              return (
                <Message
                  ref={lastMessageRef}
                  key={msg._id}
                  messages={msg}
                  receiver={receiver.id}
                  receiverData={chatRoom?.members.find(
                    (member) => member._id === receiver.id
                  )}
                  senderData={chatRoom?.members.find(
                    (member) => member._id !== receiver.id
                  )}
                />
              );
            })
          )}
          </Box>
        </Box>

        <ChatMessageInputs path={receiver.id} setChatRoom={updateChatRoom} />
      </Box>
    </>
  );
}
