import React, { createContext, useEffect, useReducer, useContext } from "react";
import useGetChats from "../hooks/useGetChats";
import {  } from "../";

const chatContext = createContext();

export default function ChatContext({ children }) {
  const {load, socket, onlineUsers} = useContext(socketContext)
  const { chatsloading, chatRooms } = useGetChats();
  const [chats, ChatsDispatch] = useReducer((state, action) => {
    switch (action.type) {
      case "ADD_CHAT":
        return [...state, action.payload];
      case "SET_CHAT":
        console.log(state, action);
        const updateChats = state.map((chat) => {
          console.log(chat._id.toString(), action.id);
          if (chat._id === action.id) {
            console.log(chat);
            chat.messages.push(action.payload);
            console.log(chat); 
            return chat;
          } else {
            return chat
          }
        })
        return updateChats;
      case "PLUCK_CHAT":
        return action.payload;
      default:
        return state;
    }
  }, []);

  useEffect(() => {
    if (!chatsloading) {
      ChatsDispatch({ type: "PLUCK_CHAT", payload: chatRooms });
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [chatsloading]);

  return (
    <chatContext.Provider value={{ chats, chatsloading, ChatsDispatch }}>
      {children}
    </chatContext.Provider>
  );
}

export { chatContext };
