import { Box } from "@mui/material";
import React, { useContext, useEffect, useState } from "react";
import ChatNavbar from "../components/chat/ChatNavbar";
import ChatMessageInputs from "../components/chat/ChatMessageInputs";
import Message from "../components/chat/Message";
import { themeContext } from "../context/ThemeContext";
import { useParams } from "react-router-dom";
import useGetChatRooms from "../hooks/useGetChatRooms";
import Nomessages from "../components/etc/Nomessages";

export default function Chat() {
  const { mode } = useContext(themeContext);
  const { loading, chatRoom } = useGetChatRooms();
  const [receiverData, setReveiverData] = useState();
  const receiver = useParams();

  // const data = async () => {
  //   if (!loading) {
  //     const isReceiver = await (chatRoom.data || []).find(
  //       (room) => room.receiverMember._id === receiver
  //     );

  //     setReveiverData(isReceiver)
  //     return isReceiver
  //   }
  // };

  useEffect(() => {
    console.log(receiver);

    if (!loading) {
      for (let i = 0; i < (chatRoom.data || []).length; i++) {
        const id = chatRoom.data[i]

        // console.log(chatRoom.data);
        // if (chatRoom.data[i].receiverMember._id === receiver) {
        //   console.log("Yes" + chatRoom.data[i].receiverMember.fullName);
        // }else {
        //   console.log("No" + chatRoom.data[i].receiverMember.fullName);
        // }
      }
    }
  }, [loading, chatRoom, receiver]);

  return (
    <Box
      sx={{
        width: "100%",
        display: "flex",
        flexDirection: "column",
        justifyContent: "space-between",
      }}
    >
      <ChatNavbar />

      <Box
        sx={{
          height: "100vh",
          overflowX: "hidden",
          overflowY: "auto",
          "&::-webkit-scrollbar": {
            height: 2,
            width: 2,
            WebkitAppearance: "none",
          },
          "&::-webkit-scrollbar-thumb": {
            borderRadius: 0,
            border: "2px solid",
          },
        }}
      >
        <Box
          sx={{
            width: "100%",
            height: "auto",
            minHeight: "100%",
            flex: 1,
            display: "flex",
            flexDirection: "column",
            justifyContent: "flex-end",
            backgroundColor: mode ? "" : "rgba(24, 24, 24, 1)",
          }}
        >
          {loading ? (
            <Nomessages />
          ) : chatRoom?.data?.length === 0 ? (
            <Nomessages />
          ) : (
            <h1>Salom</h1>
            // (chatRoom.data || []).map((roomData) => {
            //   return (
            //     <Message
            //       key={roomData._id}
            //       receiverData={roomData.receiverMember}
            //       messages={roomData.messages}
            //     />
            //   );
            // })
          )}
        </Box>
      </Box>

      <ChatMessageInputs />
    </Box>
  );
}
