import React, { useReducer, useState } from "react";
import Sidebar from "./Sidebar";
import Chat from "./Chat";
import { Route, Routes } from "react-router-dom";
import Nomessages from "../components/etc/Nomessages";
import NotFound from "../components/etc/NotFound";
import TokenContext from "../context/TokenContext";
import UserContext from "./context/UserContext";
import ChatContext from "./context/ChatContext";
import SocketContext from "./context/SocketContext";

export default function ChatApp() {
  const [active, actDispatch] = useReducer((state, action) => {
    switch (action.type) {
      case "SET_ACTIVE":
        return action.id;
      default:
        return state;
    }
  }, "");

  const [searchUser, setSearchUser] = useState([]);
  const [isSearch, setIsSearch] = useState(false);

  return (
    <>
      <TokenContext>
        <SocketContext>
          <UserContext>
            <ChatContext>
              <Sidebar
                active={active}
                searchUser={searchUser}
                setSearchUser={setSearchUser}
                setIsSearch={setIsSearch}
              />
              <Routes>
                <Route path="/">
                  <Route index element={<Nomessages sty={true} />} />
                  <Route
                    path=":id"
                    element={
                      <Chat
                        actDispatch={actDispatch}
                        isSearch={isSearch}
                        searchUser={searchUser}
                      />
                    }
                  />
                  <Route path=":id/*" element={<NotFound />} />
                </Route>
                <Route path="*" element={<NotFound />} />
              </Routes>
            </ChatContext>
          </UserContext>
        </SocketContext>
      </TokenContext>
    </>
  );
}
