import React, { useEffect, useState } from "react";
import KeyboardVoiceIcon from "@mui/icons-material/KeyboardVoice";
import StopIcon from "@mui/icons-material/Stop";
import { toast } from "react-toastify";
import "./style.css";

export default function AudioRecorder({ onTouched, setOnTouched }) {
  const [checkTouch, setCheckTouch] = useState(false);

  useEffect(() => {
    let count = null;
    if (onTouched) {
      setTimeout(() => {
        if (onTouched) {
          count = 0;
        } else {
          count = 1;
        }
      }, 600);
    }

    if (count === 1) {
      setCheckTouch(true);
    }else {
      toast.warning(
        "Please hold the mouse pressed to record a voice message"
      );
    }
  }, [onTouched]);

  return checkTouch ? (
    <div className=" absolute top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2">
      <audio hidden></audio>
      <div className="box">
        <div className="visualizer"></div>
      </div>
      <div className="play">
        <div className="btn btn-play">
          {onTouched ? (
            <StopIcon sx={{ width: "100px", fontSize: 150 }} />
          ) : (
            <KeyboardVoiceIcon sx={{ width: "100px", fontSize: 150 }} />
          )}
        </div>
      </div>
    </div>
  ) : (
    ""
  );
}
