import { Box } from "@mui/material";
import React, { useContext, useEffect, useRef, useState } from "react";
import ChatNavbar from "../components/chat/ChatNavbar";
import ChatMessageInputs from "../components/chat/ChatMessageInputs";
import Message from "../components/chat/Message";
import { themeContext } from "../context/ThemeContext";
import { useParams } from "react-router-dom";
import Nomessages from "../components/etc/Nomessages";
import { getChatRoom } from "../utils/ChatRoomApi";
import { socketContext } from "../context/SocketContext";
import { chatRoomContext } from "../context/ChatRoomContext";

export default function Chat({ onlineUsers, checkOnline }) {
  const { mode } = useContext(themeContext);
  const receiver = useParams();
  const lastMessageRef = useRef(null);

  

  

  return (
    <Box
      sx={{
        width: "100%",
        display: "flex",
        flexDirection: "column",
        justifyContent: "space-between",
      }}
    >
      <ChatNavbar
        receiverData={chatRoom?.members.find(
          (member) => member._id === receiver.id
        )}
        onlineUsers={onlineUsers}
        checkOnline={checkOnline}
      />

      <Box
        sx={{
          width: "100%",
          height: "100vh",
          overflowX: "hidden",
          overflowY: "auto",
          "&::-webkit-scrollbar": {
            height: 2,
            width: 2,
            WebkitAppearance: "none",
          },
          "&::-webkit-scrollbar-thumb": {
            borderRadius: 0,
            border: "2px solid",
          },
        }}
      >
        <Box
          sx={{
            width: "100%",
            height: "auto",
            minHeight: "100%",
            flex: 1,
            display: "flex",
            flexDirection: "column",
            justifyContent: "flex-end",
            backgroundColor: mode ? "" : "rgba(24, 24, 24, 1)",
          }}
        >
          {chatRoom?.messages.length === 0 ? (
            <Nomessages />
          ) : (
            chatRoom?.messages.map((msg, idx) => {
              return (
                <Message
                  ref={lastMessageRef}
                  key={msg._id}
                  messages={msg}
                  receiver={receiver.id}
                  receiverData={chatRoom?.members.find(
                    (member) => member._id === receiver.id
                  )}
                  senderData={chatRoom?.members.find(
                    (member) => member._id !== receiver.id
                  )}
                />
              );
            })
          )}
        </Box>
      </Box>

      <ChatMessageInputs path={receiver.id} setChatRoom={updateChatRoom} />
    </Box>
  );
}
