import React, { useContext, useEffect, useState } from "react";
import Sidebar from "./Sidebar";
import Chat from "./Chat";
import { Route, Routes } from "react-router-dom";
import Nomessages from "../components/etc/Nomessages";
import NotFound from "../components/etc/NotFound";
import { socketContext } from "../context/SocketContext";

export default function ChatApp() {
  const { onlineUsers } = useContext(socketContext);
  const [open, setOpen] = useState(false);
  const [findSearch, setFindSearch] = useState([]);
  const { room, ChatRoomDispatch } = useContext(chatRoomContext);
  const [rol, setRol] = useState("new");

  const [search, SearchDispatch] = useReducer(async (state, action) => {
    switch (action.type) {
      case "search":
        const data = await UserSearch(action.name);
        setFindSearch(data);
        return data;
      default:
        return state;
    }
  }, []);

  const checkOnline = (id) => {
    const user = onlineUsers?.find((userId) => userId === id);

    if (user && user === id) {
      return true;
    } else {
      return false;
    }
  };

  return (
    <>
      <Sidebar
        onlineUsers={onlineUsers}
        checkOnline={checkOnline}
      />
      <Routes>
        <Route path="/">
          <Route index element={<Nomessages />} />
          <Route
            path=":id"
            element={
              <Chat
                onlineUsers={onlineUsers}
                checkOnline={checkOnline}
              />
            }
          />
        </Route>
        <Route path="*" element={<NotFound />} />
      </Routes>
    </>
  );
}
